// Basic pattern
void func(int count, ...) {
    va_list ap;
    va_start(ap, count);  // 'count' is last fixed param

    for (int i = 0; i < count; i++) {
        int val = va_arg(ap, int);
        // use val...
    }

    va_end(ap);  // ALWAYS call this
}

#include <stdarg.h>
#include <stdio.h>

int sum_ints(int count, ...) {
    va_list ap;
    va_start(ap, count);

    int total = 0;
    for (int i = 0; i < count; i++) {
        // char/short promoted to int
        int v = va_arg(ap, int);
        total += v;
    }

    va_end(ap);
    return total;
}

int main(void) {
    printf("sum: %d\n", sum_ints(4, 1, 2, 3, 4));
    printf("sum: %d\n", sum_ints(3, 10, 20, 30));
    return 0;
}

#include <stdarg.h>
#include <stdio.h>

double average(int count, ...) {
    va_list ap;
    va_start(ap, count);

    double sum = 0.0;
    for (int i = 0; i < count; i++) {
        // CRITICAL: float promoted to double
        double v = va_arg(ap, double);
        sum += v;
    }

    va_end(ap);
    return count ? sum / count : 0.0;
}

int main(void) {
    // 1.0f is promoted to double automatically
    printf("avg: %.2f\n", average(3, 1.0f, 2.0f, 3.0f));
    return 0;
}

#include <stdarg.h>
#include <stdio.h>
#include <string.h>

// Concatenate strings until NULL sentinel
size_t concat_strings(char *dest, size_t size, ...) {
    va_list ap;
    va_start(ap, size);

    size_t written = 0;
    dest[0] = '\0';

    char *str;
    while ((str = va_arg(ap, char*)) != NULL) {
        size_t len = strlen(str);
        if (written + len < size) {
            strcat(dest, str);
            written += len;
        }
    }

    va_end(ap);
    return written;
}

int main(void) {
    char buf[100];
    concat_strings(buf, sizeof(buf), "Hello", " ", "World", "!", NULL);
    printf("%s\n", buf);
    return 0;
}

#include <stdarg.h>
#include <stdio.h>

void my_printf(const char *fmt, ...) {
    va_list ap;
    va_start(ap, fmt);

    for (const char *p = fmt; *p; p++) {
        if (*p != '%') {
            putchar(*p);
            continue;
        }

        switch (*++p) {
            case 'd': {
                int i = va_arg(ap, int);
                printf("%d", i);
                break;
            }
            case 'f': {
                double d = va_arg(ap, double);
                printf("%f", d);
                break;
            }
            case 's': {
                char *s = va_arg(ap, char*);
                printf("%s", s ? s : "(null)");
                break;
            }
            case '%': {
                putchar('%');
                break;
            }
            default:
                putchar('%');
                putchar(*p);
                break;
        }
    }

    va_end(ap);
}

int main(void) {
    my_printf("Name: %s, Age: %d, GPA: %f\n", 
              "Alice", 20, 3.75);
    return 0;
}

#include <stdarg.h>
#include <stdio.h>
#include <stdlib.h>

// Find max and min in one function
void minmax(int count, int *min_out, int *max_out, ...) {
    va_list ap, ap_copy;
    va_start(ap, max_out);

    // First pass: find min
    va_copy(ap_copy, ap);  // Clone for second pass
    int min = va_arg(ap, int);
    for (int i = 1; i < count; i++) {
        int v = va_arg(ap, int);
        if (v < min) min = v;
    }

    // Second pass: find max
    int max = va_arg(ap_copy, int);
    for (int i = 1; i < count; i++) {
        int v = va_arg(ap_copy, int);
        if (v > max) max = v;
    }

    va_end(ap);
    va_end(ap_copy);  // Clean up the copy too

    *min_out = min;
    *max_out = max;
}

int main(void) {
    int min, max;
    minmax(5, &min, &max, 3, 1, 4, 1, 5);
    printf("min=%d, max=%d\n", min, max);
    return 0;
}

// Add defensive logging
void debug_varargs(int count, ...) {
    va_list ap;
    va_start(ap, count);

    printf("[DEBUG] Processing %d arguments\n", count);
    for (int i = 0; i < count; i++) {
        int val = va_arg(ap, int);
        printf("[DEBUG] arg[%d] = %d\n", i, val);
    }

    va_end(ap);
}

