#include <stdarg.h>
#include <stdio.h>

// Type-safe varargs wrapper
typedef struct {
    const char *fmt;
    va_list ap;
    int initialized;
} SafeVarargs;

void safe_va_init(SafeVarargs *sv, const char *fmt, ...) {
    sv->fmt = fmt;
    va_start(sv->ap, fmt);
    sv->initialized = 1;
}

void safe_va_cleanup(SafeVarargs *sv) {
    if (sv->initialized) {
        va_end(sv->ap);
        sv->initialized = 0;
    }
}

void safe_va_print(SafeVarargs *sv) {
    if (!sv->initialized) {
        fprintf(stderr, "Error: va_list not initialized\n");
        return;
    }
    vprintf(sv->fmt, sv->ap);
}

